// Functions //
void Display_MENU_INIT(void);

void Display_MENU_INIT(void)
{
	// Static Show //
	Display_StaticShow();

	// Static dimensions //
	tft.drawFastHLine(LineHigh.xS, LineHigh.yS, LineHigh.xE - LineHigh.xS, LineHigh.Colour);
	tft.drawFastHLine(LineLow.xS, LineLow.yS, LineLow.xE - LineLow.xS, LineLow.Colour);
	tft.drawRect(RectStaticReleATemp.xS, RectStaticReleATemp.yS, RectStaticReleATemp.xE- RectStaticReleATemp.xS, RectStaticReleATemp.yE- RectStaticReleATemp.yS, RectStaticReleATemp.edgeColour);
	tft.drawFastHLine(LineDivisor.xS, LineDivisor.yS, LineDivisor.xE - LineDivisor.xS, LineDivisor.Colour);
	
	// String Home //
	DisplaySetLetterParamether(StrgHome.letterColour, StrgHome.backgroundColour, StrgHome.xS, StrgHome.yS, StrgHome.size, home);
	// Date and Time //
	UpdatedDateATime();
	// Strings Relays estates //
	DisplaySetLetterParamether(StrgRelay1Est.letterColour, StrgRelay1Est.backgroundColour, StrgRelay1Est.xS, StrgRelay1Est.yS, StrgRelay1Est.size, Relay1Est);
	DisplaySetLetterParamether(StrgRelay2Est.letterColour, StrgRelay2Est.backgroundColour, StrgRelay2Est.xS, StrgRelay2Est.yS, StrgRelay2Est.size, Relay2Est);
	DisplaySetLetterParamether(StrgRelay3Est.letterColour, StrgRelay3Est.backgroundColour, StrgRelay3Est.xS, StrgRelay3Est.yS, StrgRelay3Est.size, Relay3Est);
	DisplaySetLetterParamether(StrgRelay4Est.letterColour, StrgRelay4Est.backgroundColour, StrgRelay4Est.xS, StrgRelay4Est.yS, StrgRelay4Est.size, Relay4Est);
	UpdatedRelaysEstates();
	//tft.drawXBitmap( 5, 5, Battery, 20, 20, TFT_WHITE);

	return;
}

